<template>
    <div id="app">
      <div class='wrap'>
        <ejs-calendar id='calendar' :renderDayCell='load'></ejs-calendar>
      </div>
    </div>
  </template>
  <script>
  
  import { CalendarComponent } from "@syncfusion/ej2-vue-calendars";
  
  export default {
    name: "App",
    components: {
      "ejs-calendar": CalendarComponent
    },
    methods: {
      load: function (args) {
        let span;
        //Defines the custom HTML element to be added.
        span = document.createElement('span');
        //Uses "e-icons" class name to load Essential JS 2 built-in icons.
        span.setAttribute('class', 'e-icons highlight-day');
        if (+args.date.getDate() === 7 && +args.date.getMonth() == 3) {
          //Appends the span element to day cell.
          args.element.appendChild(span);
          //Sets the custom tooltip to the special dates.
          args.element.setAttribute('title', 'World health day!');
          //Uses "special" class name to highlight special dates that you can refer in "styles.css".
          args.element.className = 'special';
        }
        if (+args.date.getDate() === 21 && +args.date.getMonth() == 2) {
          args.element.appendChild(span);
          args.element.className = 'special';
          //Sets the custom tooltip to the special dates.
          args.element.setAttribute('title', 'World forest day');
        }
      }
    }
  }
  </script>
  <style>
  @import "../node_modules/@syncfusion/ej2-base/styles/material.css";
  @import "../node_modules/@syncfusion/ej2-buttons/styles/material.css";
  @import "../node_modules/@syncfusion/ej2-vue-calendars/styles/material.css";
  
  .wrap {
    margin: 35px auto;
    width: 240px;
  }
  
  /* Custom - styles */
  
  span.special.e-day.e-ripple-style {
    /* csslint allow: adjoining-classes*/
    font-weight: bold;
    color: red;
  }
  
  span.e-icons.highlight-day {
    /* csslint allow: adjoining-classes*/
    color: red;
    margin-top: -13px;
    display: block;
    margin-left: 4px;
  }
  
  span.e-icons.highlight-day,
  /* csslint allow: adjoining-classes*/
  span.e-icons.highlight-day:before {
    /* csslint allow: adjoining-classes*/
    color: rgb(0, 0, 255);
  }
  
  span.e-icons.highlight-day:before {
    /* csslint allow: adjoining-classes*/
    content: "\e190";
    vertical-align: middle;
    margin-right: 3px;
    font-size: 4px;
    position: relative;
    top: 1px;
    font-weight: normal;
  }
  
  span.special.e-day.e-ripple-style {
    /* csslint allow: adjoining-classes*/
    font-weight: bold;
    color: red;
  }
  
  .e-selected span.e-icons.highlight-day:before {
    /* csslint allow: adjoining-classes*/
    color: #fff;
  }
  
  @font-face {
    font-family: 'e-icons';
    src: url(data:application/x-font-ttf;charset=utf-8;base64,) format('truetype');
    font-weight: normal;
    font-style: normal;
  }
  </style>