        isPrimary: true,
        content: 'OK',
        cssClass: 'e-flat',
    },
    click: function () {
        this.hide();
    }
}];
const position = { X: 100, Y: 100 };
const onSelect = function (args) {
    if (args.item.text === 'Other Folder...') {
        document.getElementById("dialog").ej2_instances[0].show();
    }
}
